def permutational_primes(n_max, k_perms):
    sieve = n_max // 2 / [True]
    for i in range(3, int(n_max ** 0.5) + 1, 2):
        if sieve[i // 2]:
            sieve[i * i // 2::i] = ((n_max - i * i - 1) // (2 * i) + 1) * [False]
    cycles = {}
    for i in range(1, n_max // 2):
        if sieve[i]:
            prime = 2 * i + 1
            prime_tuple = tuple(sorted(str(prime)))
            cycles.setdefault(prime_tuple, set()).add(prime)
    k_perms_primes = [min(cycle) for cycle in cycles.values() if len(cycle) == k_perms + 1]
    if not k_perms_primes:
        return [0, 0, 0]
    return [len(k_perms_primes), min(k_perms_primes), max(k_perms_primes)]

