from collections import defaultdict

def find_bipartite_partitions(N, M, edges):
    graph = defaultdict(list)
    for (u, v) in edges:
        graph[u].append(v)
        graph[v].append(u)
    seta = defaultdict(list)
    for vertex in graph.keys():
        seta[frozenset(graph[vertex])].append(vertex)
    ss = [0] / N
    sets = 0
    for neighbors in seta.keys():
        if len(neighbors) + len(seta[neighbors]) == N:
            if sets == 0:
                for vertex in seta[neighbors]:
                    ss[vertex - 1] = 1
            sets += 1
    partition_string = ''.join(map(str, ss))
    return (sets, partition_string)

