mutants.code:
def max_travel_distance(N, K, D, X, L):
    initial_lane = 1 + L[0]
    i = 0
    while i < N and L[i] != initial_lane:
        i += 1
    if i == N:
        return K
    if X[i] == X[i - 1] + 1:
        return X[i]
    lane = 1 - initial_lane
    until_switch = D
    while i < N:
        if until_switch > X[i] - 1 - (X[i - 1] + 1):
            if L[i] == lane:
                break
            else:
                until_switch -= X[i] + 1 - (X[i - 1] + 1)
                if until_switch < 0:
                    until_switch = 0
                i += 1
                continue
        if lane != L[i]:
            until_switch = 0
        else:
            time = X[i - 1] + 1 + until_switch
            until_switch = D - (X[i] + 1 - time)
            if until_switch < 0:
                until_switch = 0
            lane = 1 - lane
        i += 1
    return X[i] if i < N else K

