def find_final_value(a, n):
    """"""
    if n == 1:
        return a[0]
    nums = a
    for _ in range(n - 1):
        diff = []
        for i in range(len(nums) - 1):
            cur = nums[i + 1] - nums[i]
            if cur:
                diff.append(cur)
        if not diff:
            return 0
        if len(diff) == 1:
            return diff[0]
        nums = sorted(diff)
    return nums[0]
