    c = 1
    for i in range(0, n // 2):
        if s[i] == s[n - i - 1]:
            c += 1
        else:
            break
    if c > k:
        return 'YES'
    elif c == k:
        if n % 2 != 0:
            return 'YES'
        elif s == s[::-1]:
            return 'NO'
        else:
            return 'YES'
    else:
        return 'NO'

