from math import ceil, gcd
from functools import reduce

def can_find_half_sequence(N, A):
    size = ceil(N / 2)
    multiples_2 = [v for v in A if v % 2 == 0]
    if len(multiples_2) <= size:
        return 'NO'
    if reduce(gcd, multiples_2) > 1:
        return 'NO'
    triats = [multiples_2[0]]
    g = multiples_2[0]
    for v in multiples_2:
        nou_g = gcd(g, v)
        if nou_g < g:
            triats.append(v)
            g = nou_g
            if g == 1:
                break
    return 'YES' if len(triats) <= size else 'NO'

