from collections import defaultdict
import bisect

def find_secret_message_occurrences(s: str) -> int:
    ans = 1
    ci = defaultdict(list)
    for (i, c) in enumerate(s):
        ci[c].append(i)
    for v in ci.values():
        ans = max(ans, len(v))
    cs = list(ci.keys())
    for (a, b) in product(cs, repeat=2):
        t = 0
        for i in ci[a]:
            t += len(ci[b]) - bisect.bisect_right(ci[b], i)
        ans = max(ans, t)
    return ans

