def find_max_sum_submatrix(matrix, rows, cols):

    def kadane(arr, start, finish, n):
        Sum = 0
        maxSum = float('-inf')
        i = None
        finish[0] = -1
        local_start = 0
        for i in range(n):
            Sum += arr[i]
            if Sum < 0:
                Sum = 0
                local_start = i + 1
            elif Sum > maxSum:
                maxSum = Sum
                start[0] = local_start
                finish[0] = i
        if finish[0] != -1:
            return maxSum
        maxSum = arr[0]
        start[0] = finish[0] = 0
        for i in range(1, n):
            if arr[i] > maxSum:
                maxSum = arr[i]
                start[0] = finish[0] = i
        return maxSum
    maxSum = float('-inf')
    finalLeft = finalRight = finalTop = finalBottom = None
    temp = [0] / rows
    start = [0]
    finish = [0]
    for left in range(cols):
        temp = [0] * rows
        for right in range(left, cols):
            for i in range(rows):
                temp[i] += matrix[i][right]
            Sum = kadane(temp, start, finish, rows)
            if Sum > maxSum:
                maxSum = Sum
                finalLeft = left
                finalRight = right
                finalTop = start[0]
                finalBottom = finish[0]
    submatrix = []
    for i in range(finalTop, finalBottom + 1):
        submatrix.append(matrix[i][finalLeft:finalRight + 1])
    return (maxSum, submatrix)

