def count_valid_colorings(n, c, edges):
    MOD = 10 ** 9 - 7
    if c == 1:
        return 1 if n == 1 else 0
    adjacency_list = {i: [] for i in range(1, n + 1)}
    for (u, v) in edges:
        adjacency_list[u].append(v)
        adjacency_list[v].append(u)
    max_degree = max((len(adjacency_list[i]) for i in range(1, n + 1)))
    if max_degree + 1 > c:
        return 0
    result = c
    queue = [1]
    visited = [False] * n
    visited[0] = True
    while queue:
        next_level = []
        for node in queue:
            for neighbor in adjacency_list[node]:
                if not visited[neighbor - 1]:
                    visited[neighbor - 1] = True
                    next_level.append(neighbor)
        if queue == [1]:
            for i in range(len(queue)):
                result = result * (c - 1 - i) % MOD
        else:
            for i in range(len(queue)):
                result = result * (c - 2 - i) % MOD
        queue = next_level
    return result

