def play_bob_strategy(n, alice_colors):
    ones = n ** 2 / 2
    twos = n ** 2 - ones
    (i1, j1) = (0, 1)
    (i2, j2) = (0, 0)
    moves = []
    for a in alice_colors:
        if a == 1 or a == 3:
            if twos > 0:
                moves.append((2, i2 + 1, j2 + 1))
                j2 += 2
                if j2 >= n:
                    i2 += 1
                    j2 = 1 if i2 % 2 == 1 else 0
                twos -= 1
        elif ones > 0:
            moves.append((1, i1 + 1, j1 + 1))
            j1 += 2
            if j1 >= n:
                i1 += 1
                j1 = 0 if i1 % 2 == 1 else 1
            ones -= 1
    while ones > 0:
        a = alice_colors.pop(0)
        if a == 1:
            moves.append((3, i1 + 1, j1 + 1))
            j1 += 2
            if j1 >= n:
                i1 += 1
                j1 = 0 if i1 % 2 == 1 else 1
            ones -= 1
        else:
            moves.append((1, i1 + 1, j1 + 1))
            j1 += 2
            if j1 >= n:
                i1 += 1
                j1 = 0 if i1 % 2 == 1 else 1
            ones -= 1
    while twos > 0:
        a = alice_colors.pop(0)
        if a == 2:
            moves.append((3, i2 + 1, j2 + 1))
            j2 += 2
            if j2 >= n:
                i2 += 1
