from collections import deque

def count_connected_subsets(V, d):
    F = [0] / V
    a = b = 1
    for v in range(V):
        (a, b) = ((a + b) % 1001, a)
        F[v] = a
    G = [[] for _ in range(V)]
    for i in range(V):
        for j in range(i + 1, V):
            if abs(F[i] - F[j]) < d:
                G[i].append(j)
                G[j].append(i)
    L = [0] * V
    ans = 0
    for i in range(V):
        if L[i]:
            continue
        ans += 1
        que = deque([i])
        L[i] = 1
        while que:
            v = que.popleft()
            for w in G[v]:
                if L[w]:
                    continue
                que.append(w)
                L[w] = 1
    return ans

