    a = b = 1
    for v in range(V):
        (a, b) = ((a + b) % 1001, a)
        F[v] = a
    G = [[] for _ in range(V)]
    for i in range(V):
        for j in range(i + 1, V):
            if abs(F[i] - F[j]) <= d:
                G[i].append(j)
                G[j].append(i)
