    ans = 0
    for i in range(V):
        if L[i]:
            continue
        ans += 1
        que = deque([i])
        L[i] = 1
        while que:
            v = que.popleft()
            for w in G[v]:
                if L[w]:
                    continue
                que.append(w)
                L[w] = 1
    return ans

