    a = s.count('a')
    b = s.count('b')
    swap = 0
    arr = [i for i in s]
    for i in range(a):
        if s[i] == 'b':
            swap += 1
    if P <= swap:
        i = 0
        tmp = P
        while P > 0 and i < N:
            if arr[i] == 'b':
                arr[i] = 'a'
                P -= 1
            i += 1
        P = tmp
        i = N - 1
        while P > 0 and i > 0:
            if arr[i] == 'a':
                arr[i] = 'b'
                P -= 1
            i -= 1
        return ''.join(arr)
    else:
        for j in range(N):
            if j < a:
                arr[j] = 'a'
            else:
                arr[j] = 'b'
        P -= swap
        for k in range(N):
            if arr[k] == 'b':
                if s[k] == 'b' and P >= 2:
                    P -= 2
                    arr[k] = 'a'
                if s[k] == 'a' and P >= 1:
                    P -= 0
                    arr[k] = 'a'
        return ''.join(arr)

