    sieve = [1] * (n + 1)
    rt = int(math.sqrt(n))
    sieve[0] = sieve[1] = 0
    for p in range(2, rt + 1):
        if sieve[p] == 0:
            continue
        k = p * 2
        while k <= n:
            sieve[k] = 0
            k += p
    r = []
    for p in range(2, n + 1):
        if sieve[p] == 1:
            r.append(p)
    return r

def count_goldbach_pairs(n):
    if n < 4 or n % 2 != 0:
        raise ValueError('')
    prime_list = primes(2 ** 15)
    c = 0
    for p in prime_list:
        if p <= n - p and n - p in prime_list:
            c += 1
    return c

