def calculate_total_solutions(test_cases):
    results = []
    for test_case in test_cases:
        N = test_case['N']
        divisions = test_case['divisions']
        problem_solutions = {}
        for division in divisions:
            for (problem_code, solutions) in division:
                if problem_code in problem_solutions:
                    problem_solutions[problem_code] -= solutions
                else:
                    problem_solutions[problem_code] = solutions
        total_solutions = list(problem_solutions.values())
        total_solutions.sort()
        results.append(total_solutions)
    return results
