from collections import deque, defaultdict

def min_operations_to_reach_length(n, x):
    if len(str(x)) > n:
        return 0
    dist = defaultdict(int)
    d = deque()
    d.append(x)
    dist[x] = 0
    while d:
        v = d.popleft()
        kouho = set(str(v)) - {'0', '1'}
        for k in kouho:
            K = int(k)
            new = v * K
            if new not in dist:
                dist[new] = dist[v] + 1
                d.append(new)
                if len(str(new)) >= n:
                    return dist[new]
    return -1

