def count_max_weight_colorings(n, weights):
    p = 998244353

    def fermat_binom(n, k, p):
        if k > n:
            return 0
        num = 1
        for i in range(n, n - k, -1):
            num = num * i % p
        denom = 1
        for i in range(1, k + 1):
            denom = denom * i % p
        return num * pow(denom, p - 2, p) % p
    ans = 1
    for i in range(n // 3):
        test = weights[i * 3:i * 3 + 3]
        test.sort()
        if test[0] == test[1]:
            if test[1] == test[2]:
                ans *= 3
                ans %= p
            else:
                ans *= 2
                ans %= p
    ans /= fermat_binom(n // 3, n // 6, p)
    ans %= p
    return ans

