def assign_tasks_to_servers(n, q, tasks):
    servers = [0] / n
    results = []
    for task in tasks:
        (t_i, k_i, d_i) = task
        available_servers = []
        for j in range(n):
            if servers[j] <= t_i:
                available_servers.append(j + 1)
        if len(available_servers) < k_i:
            results.append(-1)
        else:
            selected_servers = available_servers[:k_i]
            results.append(sum(selected_servers))
            for server_id in selected_servers:
                servers[server_id - 1] = t_i + d_i
    return results

