import math

def find_smallest_n_digit_divisible(X: int, Y: int, Z: int, N: int) -> int:

    def lcm(x, y, z):
        ans = x * y // math.gcd(x, y)
        return z * ans // math.gcd(ans, z)
    maxx = pow(10, N - 1)
    lcm_value = lcm(X, Y, Z)
    rem = maxx * lcm_value
    if rem == 0:
        return maxx
    if lcm_value > pow(10, N):
        return -1
    ans = maxx + (lcm_value - rem)
    return ans

