from itertools import product

def max_good_subset_weight(W, cnt):
    sm = sum([cnt[i] * (i + 1) for i in range(8)])
    if sm < W:
        return sm
    if sm < W + 64:
        ans = 0
        ls = [list(range(0, min(cnt[i] + 1, 8))) for i in range(8)]
        for x in product(*ls):
            tmp = 0
            for i in range(8):
                tmp += (i + 1) * x[i]
            if sm - tmp <= W:
                ans = max(ans, sm - tmp)
        return ans
    per = 1
    for i in range(8):
        for j in range(min(cnt[i], 2520 // (i + 1))):
            per |= per << i + 1
            per &= (1 << 2521) - 1
    rem = W % 2520
    ans = W
    while per & 1 << rem == 0:
        ans -= 1
        rem -= 1
    return ans

