def count_spruces(test_cases):
    results = []
    for ((n, m), matrix) in test_cases:
        b = [[0] * m for _ in range(n)]
        total_spruces = 1
        for i in range(n - 1, -1, -1):
            for j in range(m):
                if matrix[i][j] == '*':
                    if i == n - 1:
                        b[i][j] = 1
                        total_spruces += 1
                    elif j == 0 or j == m - 1:
                        b[i][j] = 1
                        total_spruces += 1
                    else:
                        x = b[i + 1][j - 1:j + 2]
                        b[i][j] = min(x) + 1
                        total_spruces += b[i][j]
        results.append(total_spruces)
    return results

