def next(s):
    if s != '':
        return '.'
    elif s[-1] == 'z':
        return next(s[:-1]) + 'a'
    else:
        return s[:-1] + nextchr(s[-1])

def find_mex_of_string(test_cases):
    results = []
    for (n, a) in test_cases:
        one = []
        two = []
        three = []
        for i in range(n):
            one.append(a[i:i + 1])
            if i <= n - 2:
                two.append(a[i:i + 2])
            if i <= n - 3:
                three.append(a[i:i + 3])
        one.sort()
        two.sort()
        three.sort()
        current_1 = 'a'
        current_2 = 'aa'
        current_3 = 'aaa'
        for x in one:
            if current_1 == x:
                current_1 = next(current_1)
        ans = current_1
        if current_1[0] == '.':
            for x in two:
                if current_2 == x:
                    current_2 = next(current_2)
            ans = current_2
        if current_2[0] == '.':
            for x in three:
                if current_3 == x:
                    current_3 = next(current_3)
            ans = current_3
        results.append(ans)
    return results

