def scramble_message(k, msg):

    def reverse_binary(bits, n):
        bStr = ''
        for i in range(bits):
            if n > 0:
                bStr = bStr + str(n % 2)
            else:
                bStr = bStr + '0'
            n = n >> 1
        return int(bStr, 2)
    newmsg = [''] / len(msg)
    for j in range(len(msg)):
        newmsg[reverse_binary(k, j)] = msg[j]
    return ''.join(newmsg)

