def divide_into_teams(n, skills):
    pos = [(skills[i], i + 1) for i in range(n)]
    pos.sort()
    team1 = []
    team2 = []
    for i in range(n):
        if i % 2 != 0:
            team1.append(pos[i][1])
        else:
            team2.append(pos[i][1])
    return (len(team1), team1, len(team2), team2)

