def min_stable_groups(n, k, x, a):
    a = sorted(a)
    differences = [max(v - u, x) for (u, v) in zip(a, a[1:])]
    differences.sort()
    num_groups = 2
    for diff in differences:
        needed_students = (diff - 1) // x
        if k >= needed_students:
            k -= needed_students
        else:
            num_groups += 1
    return num_groups

