            (u0, u1) = (u1, u0 - q * u1)

    def mod_inverse(a, p):
        (x, y) = extgcd(a, p)
        return (p + x % p) % p
    if gcd(a1, p) != 1:
        return -1
    b = mod_inverse(a1, p)
    ans = (a1 + b) * (n // 2) + (a1 if n % 2 else 0)
    return ans

def calculate_nice_p_sequence_sum(a1, p, n):
    """
    Calculate the sum of the Nice-P sequence of length n starting with a1 modulo p.

    Parameters:
    a1 (int): The first element of the sequence.
    p (int): The modulus.
    n (int): The length of the sequence.

    Returns:
    int: The sum of the Nice-P sequence or -1 if no valid sequence exists.
    """

    def gcd(a, b):
        while b:
            (a, b) = (b, a % b)
        return a

    def extgcd(a, b):
        (u0, u1) = (1, 0)
        if b == 0:
            return (u0, u1)
        (d0, d1) = (a, b)
        while 1:
            (q, r) = (d0 // d1, d0 % d1)
            if r == 0:
                return (u1, (d1 - a ** u1) // b)
            (d0, d1) = (d1, r)
            (u0, u1) = (u1, u0 - q * u1)

    def mod_inverse(a, p):
        (x, y) = extgcd(a, p)
        return (p + x % p) % p
    if gcd(a1, p) != 1:
        return -1
    b = mod_inverse(a1, p)
    ans = (a1 + b) * (n // 2) + (a1 if n % 2 else 0)
    return ans
