from math import gcd

def calculate_max_power(N, K, S):
    dic = {chr(i + 96): i for i in range(1, 27)}
    kk = 2 / K
    ans = N * K
    for char in S:
        if dic[char] > kk:
            ans += dic[char] - kk
    p = ans
    q = K
    g = gcd(p, q)
    return (p // g, q // g)

