def min_operations_to_equal(A: int, B: int, N: int) -> int:
    """"""
    N -= 1
    diff = A ^ B
    bits = len(bin(diff)[2:])
    pwr = pow(2, bits - 1)
    if A == B:
        return 0
    elif diff <= N:
        return 1
    elif pwr <= N:
        return 2
    else:
        return -1
