        if n == 9 or n == 8:
            return 7
        for p in range(n, 2 * n):
            for i in range(2, p):
                if p % i == 0:
                    break
            else:
                return p
        return None
    results = []
    for case in test_cases:
        (N, current_code) = case
        count = 0
        for digit in current_code:
            digit = int(digit)
            if digit != -1:
                if digit == 1:
                    count += 1
                else:
                    count += abs(digit - check_and_change_to_prime(digit))
        results.append(count)
    return results

