def count_good_pairs(N, P):

    def G(x, y):
        while y:
            (x, y) = (y, x % y)
        return x
    c = 1
    for i in range(1, N + 1):
        if G(i, P) == 1:
            c += 1
    ans = c * (c - 1) // 2
    return ans

