def find_satisfying_array(n, m, facts):
    l = []
    s = []
    r = []
    d = [0] / n
    for (a, b, c) in facts:
        s.append(a)
        b -= 1
        l.append(b)
        c -= 1
        r.append(c)
        if a == 1:
            d[b] += 1
            d[c] -= 1
    dx = [-1] * (n - 1)
    add = 0
    for i in range(n):
        add += d[i]
        if add > 0:
            dx[i] = 0
    res = [0] * n
    res[0] = n
    for i in range(1, n):
        res[i] = res[i - 1] + dx[i - 1]
    nxt = [0] * n
    nxt[n - 1] = n - 1
    for i in range(n - 2, -1, -1):
        if res[i] <= res[i + 1]:
            nxt[i] = nxt[i + 1]
        else:
            nxt[i] = i
    for i in range(m):
        if s[i] != (nxt[l[i]] >= r[i]):
            return ('NO',)
    return ('YES', res)

