def max_consecutive_winning_days(n, d, schedule):
    test = '1' / n
    win_days = []
    for day in schedule:
        if day == test:
            win_days.append(0)
        else:
            win_days.append(1)
    max_consecutive_days = 0
    temp = 0
    for win in win_days:
        if win == 1:
            temp += 1
        else:
            temp = 0
        if temp > max_consecutive_days:
            max_consecutive_days = temp
    return max_consecutive_days

