def count_valid_playlists(n, T, songs):
    MOD = 1000000008

    def findsum(comb):
        return sum((song[0] for song in comb))

    def finda(a, b, c):
        if a == 0:
            return 0
        if a == 1 and b == 0 and (c == 0):
            return 1
        return a * findb(a - 1, b, c) + a * findc(a - 1, b, c)

    def findb(a, b, c):
        if b == 0:
            return 0
        if b == 1 and a == 0 and (c == 0):
            return 1
        return b * finda(a, b - 1, c) + b * findc(a, b - 1, c)

    def findc(a, b, c):
        if c == 0:
            return 0
        if c == 1 and a == 0 and (b == 0):
            return 1
        return c * finda(a, b, c - 1) + c * findb(a, b, c - 1)
    total_combinations = 0
    from itertools import combinations
    for i in range(1, n + 1):
        allcomb = list(combinations(songs, i))
        for comb in allcomb:
            if findsum(comb) == T:
                a = sum((1 for song in comb if song[1] == 1))
                b = sum((1 for song in comb if song[1] == 2))
                c = sum((1 for song in comb if song[1] == 3))
                total_combinations += finda(a, b, c) + findb(a, b, c) + findc(a, b, c)
    return total_combinations % MOD

