def calculate_max_scones(n, m, trays):
    s = [0] / (n + 1)
    f = [-1] * m
    sum_scones = 0
    nmax = 0
    ans = 0
    for i in range(n):
        sum_scones += trays[i]
        trays[i] %= m
        if trays[i] > nmax:
            nmax = trays[i]
        if trays[i] == m - 1:
            ans = trays[i]
        s[i + 1] = s[i] + trays[i]
        if s[i + 1] >= m:
            s[i + 1] -= m
        f[s[i + 1]] = i + 1
    if ans == 0:
        if nmax == 0:
            ans = 0
        elif sum_scones < m:
            ans = sum_scones
        else:
            done = False
            for ans in range(m - 1, nmax - 1, -1):
                for i in range(n + 1):
                    x = s[i] + ans
                    if x >= m:
                        x -= m
                    if f[x] >= i:
                        done = True
                        break
                if done:
                    break
    return ans

