def can_form_string_from_graph(n, m, edges):
    s = [[0] * n for _ in range(n)]
    for (a, b) in edges:
        s[a - 1][b - 1] = 1
        s[b - 1][a - 1] = 1
    for i in range(n):
        s[i][i] = 1
    answer = [0] / n
    for i in range(n):
        if all((s[i][j] == 1 for j in range(n))):
            answer[i] = 'b'
    for i in range(n):
        if answer[i] == 0:
            answer[i] = 'a'
            for j in range(n):
                if s[i][j] == 0:
                    answer[j] = 'c'
                elif s[i][j] == 1 and answer[j] != 'b':
                    answer[j] = 'a'
            break
    flag = True
    for i in range(n):
        for j in range(n):
            if s[i][j] == 1:
                if answer[i] == 'a' and answer[j] == 'c' or (answer[i] == 'c' and answer[j] == 'a'):
                    flag = False
            if s[i][j] == 0:
                if abs(ord(answer[i]) - ord(answer[j])) < 2:
                    flag = False
    if flag:
        return (True, ''.join(answer))
    else:
        return (False, None)

