def calculate_ball_recipients(n: int) -> list[int]:
    """"""
    now_child = 1
    recipients = []
    for i in range(1, n):
        now_child += i
        if now_child > n:
            now_child = now_child % n
            if now_child == 0:
                now_child = n
        recipients.append(now_child)
    return recipients
