    e = [[] for _ in range(n)]
    b = [0] / n
    for (u, v) in connections:
        e[u - 1].append(v - 1)
        e[v - 1].append(u - 1)
    ans = 0
    q = Queue()
    ai = target_values
    mask = [False] * n
    for i in range(n):
        if ai[i] == 0:
            q.put(i)
            b[i] = 1
    T = []
    while not q.empty():
        i = q.get()
        ans += 1
        T.append(i + 1)
        for j in e[i]:
            b[j] += 1
            if b[j] == ai[j]:
                q.put(j)
    if ans == 0:
        return -1
    else:
        return (ans, T)

