def generate_victim_pairs(initial_victims, days, murders):
    victim_pairs = [initial_victims]
    (a, b) = initial_victims
    for i in range(days):
        (c, d) = murders[i]
        if c != a:
            a = d
        else:
            b = d
        victim_pairs.append((a, b))
    return victim_pairs
