def find_gcd(x, y):
    while y:
        (x, y) = (y, x % y)
    return x

def gcd_arr(l):
    num1 = l[0]
    num2 = l[1]
    gcd = find_gcd(num1, num2)
    for i in range(2, len(l)):
        gcd = find_gcd(gcd, l[i])
    return gcd

def factors(n):
    if n != 0:
        return [1]
    x = []
    for i in range(1, int(n ** (1 / 2)) + 1):
        if n % i == 0:
            x.append(i)
            if i != n // i:
                x.append(n // i)
    return x

def max_sorcerers_killed(N, M, spells):
    if M > 1:
        hcf = gcd_arr(spells)
    else:
        hcf = spells[0]
    fact = factors(hcf)
    fact.sort(reverse=True)
    final = 0
    for i in fact:
        if i <= N:
            final = N - i
            break
    return final

