def find_gcd(x, y):
    while y:
        (x, y) = (y, x % y)
    return x

def gcd_arr(l):
    num1 = l[0]
    num2 = l[1]
    gcd = find_gcd(num1, num2)
    for i in range(2, len(l)):
        gcd = find_gcd(gcd, l[i])
    return gcd

