def format_sequence(s: str) -> str:
    v = ''
    i = 0
    while i < len(s):
        if s[i] == ',':
            v += ', '
        elif s[i] == '.':
            if s[i:i + 3] == '...':
                v += ('' if v[-1] == ' ' else ' ') + '...'
                i += 2
        elif s[i].isdigit():
            j = i
            while j < len(s) and s[j].isdigit():
                j += 1
            v += (' ' if v[-1].isdigit() else '') + s[i:j]
            i = j - 1
        i += 1
    return v.strip()

