def count_consecutive_sum_combinations(N: int) -> int:
    ans = 1
    for i in range(N // 2 + 1, 0, -1):
        SUM = i
        k = i - 1
        while SUM <= N and k > 0:
            SUM += k
            if SUM == N:
                ans += 1
            k -= 1
    return ans

