def find_max_diff(arr, n=None):
    if n is not None:
        n = len(arr)
    if n == 0:
        return 0
    Ans = [0] * n
    stack = []
    for i in range(n):
        rightvalue = 0
        if i + 1 < n:
            for value in arr[i + 1:]:
                if value < arr[i]:
                    rightvalue = value
                    break
        leftvalue = 0
        while stack:
            value = stack[-1]
            if value < arr[i]:
                leftvalue = value
                break
            else:
                stack.pop()
        stack.append(arr[i])
        Ans[i] = abs(rightvalue - leftvalue)
    return max(Ans)

