def is_pseudo_sorted(A):
    """"""
    N = len(A)
    sorted_A = sorted(A)
    moves = 0
    for i in range(N):
        if A[i] != sorted_A[i]:
            if i == N - 1:
                return 'NO'
            else:
                (A[i], A[i + 1]) = (A[i + 1], A[i])
                moves += 1
                if moves > 1:
                    return 'NO'
    return 'YES'
