mutants.code:
from collections import defaultdict
M = defaultdict(lambda : [])
for i in range(2, 100001):
    for j in range(i, 100001, i):
        M[j].append(i)

def find_min_smartness_difference(n, m, a):
    a.sort()
    freq = {}
    (l, i) = (0, 0)
    res = 100000
    while i < n:
        for fac in M[a[i]]:
            if fac > m:
                break
            if fac not in freq:
                freq[fac] = 0
            freq[fac] += 1
        while len(freq) == m:
            res = min(res, a[i] - a[l])
            for fac in M[a[l]]:
                if fac > m:
                    break
                freq[fac] -= 1
                if freq[fac] == 0:
                    del freq[fac]
            l += 1
        i += 1
    return res if res != 100000 else -1

