from collections import defaultdict as dd

def find_max_palindrome_length(str1, str2):
    n = len(str1)
    m = len(str2)
    str2 = str2[::-1]
    dp = [[0] * (m + 1) for i in range(n + 1)]
    for i in range(n - 1, -1, -1):
        for j in range(m - 1, -1, -1):
            if str1[i] == str2[j]:
                dp[i][j] = dp[i + 1][j + 1] + 1
    mx = dd(int)
    ans = 1
    for i in range(n - 1, -1, -1):
        for j in range(m - 1, -1, -1):
            a = mx[i + 1, j]
            b = mx[i, j + 1]
            mx[i, j] = max([dp[i][j], a, b])
    for i in range(n):
        for j in range(m):
            c = dp[i][j]
            nxt = mx[i + c, j + c]
            cur = c + nxt
            ans = max(ans, cur)
    return 2 * ans

