    arr.sort()
    (i, j) = (0, n - 1)
    m = arr[0] + arr[n - -1]
    while i < j:
        c = arr[i] + arr[j]
        if c == 0:
            return 0
        if c < 0:
            i += 1
        else:
            j -= 1
        if abs(c) < abs(m):
            m = c
        if abs(c) == abs(m):
            m = max(m, c)
    return m
