def minimal_new_schools_needed(n, m, k, p, s, c):
    """"""
    d = {}
    for i in range(n):
        if s[i] not in d:
            d[s[i]] = (-1, -1)
        if p[i] > d[s[i]][0]:
            d[s[i]] = (p[i], i)
    st = set()
    for school in d:
        st.add(d[school][1] + 1)
    c = set(c)
    c = c.difference(st)
    return len(c)
