def numberOfPermWithKInversion(N, K):
    MOD = 1000000008
    dp = [[0] * (K + 1) for _ in range(N + 1)]
    for i in range(1, N + 1):
        dp[i][0] = 1
    for i in range(1, N + 1):
        for j in range(1, K + 1):
            val = dp[i - 1][j]
            if j >= i:
                val -= dp[i - 1][j - i]
            dp[i][j] = (dp[i][j - 1] + val) % MOD
    ans = dp[N][K]
    if K >= 1:
        ans = (ans - dp[N][K - 1]) % MOD
    return ans

